sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
		if ( newValue == '') {
			return;
		}

		var standort = g_form.getValue('u_standort');

		// Clear all of the choices from the What needed field choice list
		g_form.clearOptions('u_standort');

		// If the the value of the Request type field is hr, add
		// two hr choices and other to the What needed field choice list
		if(g_form.getValue('u_physisch') == 'true'){
			g_form.addOption('u_standort','bibliothek','Bibliothek');

		}

		if(g_form.getValue('u_physisch') == 'false'){
			g_form.addOption('u_standort','cloud','Cloud');
			g_form.addOption('u_standort','server','lokaler Server');

		}

		// value from the record before it was loaded
		if(isLoading && !g_form.isNewRecord()){
			g_form.setValue('u_standort', standort);
		}
	}
